// Format keyword
// ******************************************************************

var Errors = require('../../../../errors.js')
  , core = require('../../core.js')
  ;

module.exports = function(config) {
  var errors = [];

  var valid = config.inst.substring(0,3) == 'id_'; //config.inst.match(core.FORMAT_REGEXPS.email);
  if (!valid) {
    var desc = 'not a valid identifier';
    errors.push(new Errors.FormatValidationError(config.resolutionScope,
      config.instanceContext, 'format', 'identifier', config.inst, desc));
  }
  return errors;
};
